<div class="border-gray-700">
    <div class="md:space-x-1 flex flex-wrap">
        <div class="flex flex-wrap">
            <div class="flex items-center m-2">
                <input id="inline-radio-1" type="radio" value="all" name="dataTable[filter][status]" class="w-4 h-4 text-primary-600 bg-gray-100 border-gray-300 focus:ring-primary-500 dark:focus:ring-primary-600 dark:ring-offset-gray-800 focus:ring-2 dark:bg-gray-700 dark:border-gray-600">
                <label for="inline-radio-1" class="ml-2 text-sm font-medium text-gray-900 dark:text-gray-300">All</label>
            </div>
            <div class="flex items-center m-2">
                <input id="inline-radio-2" type="radio" value="1" name="dataTable[filter][status]" class="w-4 h-4 text-primary-600 bg-gray-100 border-gray-300 focus:ring-primary-500 dark:focus:ring-primary-600 dark:ring-offset-gray-800 focus:ring-2 dark:bg-gray-700 dark:border-gray-600">
                <label for="inline-radio-2" class="ml-2 text-sm font-medium text-gray-900 dark:text-gray-300">Active</label>
            </div>
            <div class="flex items-center m-2">
                <input id="inline-radio-3" type="radio" value="0" name="dataTable[filter][status]" class="w-4 h-4 text-primary-600 bg-gray-100 border-gray-300 focus:ring-primary-500 dark:focus:ring-primary-600 dark:ring-offset-gray-800 focus:ring-2 dark:bg-gray-700 dark:border-gray-600">
                <label for="inline-radio-3" class="ml-2 text-sm font-medium text-gray-900 dark:text-gray-300">In-Active</label>
            </div>
        </div>
    </div>
